import React, { AsyncStorage } from 'react';
import {Alert, Button, Image, StyleSheet, Text, View } from 'react-native';
import Ionicons from 'react-native-vector-icons/Ionicons';
import {
  heightPercentageToDP as hp,
  widthPercentageToDP as wp,
} from 'react-native-responsive-screen';

export default function LogoutButton({navigation}) {

  async function confirmLogout() {
    Alert.alert("Logout ?", "are you sure ?", [
      {
        text: 'cancel',
      },
      {
        text: 'ok',
        onPress: () => AsyncStorage.clear()
      }
    ])
  }

  return (
    <View style={styles.container}>
      <View style={styles.logoWrapper}>
        <Image
          source={require('../assets/logo-about.png')}
          style={styles.logoImage}
        />
        <Text style={styles.logoText}>M O B I L E</Text>
      </View>
      <View style={styles.aboutContainer}>
        <Text style={styles.aboutText}>
          Thank you for using Pelita Bangsa Mobile App.
          This application is made for personal use and unassociated with Pelita Bangsa University.
        </Text>
        <Text>Feedback and Collaboration</Text>
        <Ionicons name="ios-mail" />
        <Ionicons name="ios-whatsapp" />
      </View>

      <Button
        title="Logout"
        color="red"
        onPress={() => confirmLogout()}
      />
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    justifyContent: 'center',
    alignItems: 'center',
    flexWrap: 'wrap'
  },
  logoImage: {
    width: wp(50),
    height: hp(30)
  },
  logoWrapper: {
    alignItems: 'center',
    width: wp(70),
    height: hp(30),
    marginBottom: hp(9),
    marginTop: hp(3)
  },
  logoText: {
    fontWeight: 'bold',
    fontFamily: 'DamascusBold',
    fontSize: wp(5),
    color: '#1A1B8A'
  },
  aboutContainer: {
    width: wp(85),
    alignItems: 'center',
    // marginBottom: hp(9),
    // marginTop: hp(3)
  },
  aboutText: {
    textAlign: 'justify',
    marginBottom: hp(5)
  }
})
