import React, { useEffect, useState, createContext } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Ionicons from 'react-native-vector-icons/Ionicons';
import {
  Home, Login,
  Notes, Register,
  Schedules
} from './pages';
import AsyncStorage from '@react-native-community/async-storage';
import { Alert, Button, View } from 'react-native';

const Tab = createBottomTabNavigator();
export const userContext = createContext();

function App(props) {
  const [userData, setUserData] = useState();

  useEffect(() => {
    getData();
  }, []);

  console.log(props)
  async function getData() {
    let data = await AsyncStorage.getItem("@UserData");
    if(data) {
      const {name, email, photo} = JSON.parse(data);
      setUserData({name, email, photo});
    }
  }

  async function setData(data) {
    await AsyncStorage.setItem("@UserData", JSON.stringify(data));
    setUserData(data);
  }

  async function confirmLogout() {
    Alert.alert("Logout ?", "are you sure ?", [
      {
        text: 'cancel',
      },
      {
        text: 'ok',
        onPress: () => AsyncStorage.clear()
      }
    ])
  }

  return (
    <userContext.Provider value={{
      setData: (data) => setData(data)
    }}>
      <NavigationContainer>
        {!userData ? (
          <Tab.Navigator>
            <Tab.Screen
              name="Login"
              component={Login}
              options={{tabBarIcon: ({color, size}) => (
                <Ionicons name="ios-key" color={color} size={size} />
             )}}
            />
            <Tab.Screen
              name="Register"
              component={Register}
              options={{tabBarIcon: ({color, size}) => (
                <Ionicons name="ios-create" color={color} size={size} />
             )}}
            />
          </Tab.Navigator>
        ) : (
          <Tab.Navigator>
            <Tab.Screen
              name="Home"
              component={Home}
              options={{tabBarIcon: ({color, size}) => (
                 <Ionicons name="ios-school" color={color} size={size} />
              )}}
            />
            <Tab.Screen
              name="Notes"
              component={Notes}
              options={{tabBarIcon: ({color, size}) => (
                 <Ionicons name="ios-book" color={color} size={size} />
              )}}
            />
            <Tab.Screen
              name="Schedules"
              component={Schedules}
              options={{tabBarIcon: ({color, size}) => (
                 <Ionicons name="ios-calendar" color={color} size={size} />
              )}}
            />
            <Tab.Screen
              name="Logout"
              component={Schedules}
              options={{tabBarIcon: ({color, size}) => (
                 <Ionicons name="ios-calendar" color={color} size={size} onPress={() => confirmLogout()} />
              )}}
            />
          </Tab.Navigator>
        )}
      </NavigationContainer>
    </userContext.Provider>
  );
}

export default App;
