const initialState = {
  user: '',
  email: '',
  notes: [],
  activities: []
}

function userReducer(state = initialState, action) {
  switch (action.type) {
    case 'FETCH_DATA':
      return {
        ...state,
        user: action.payload.user,
        email: action.payload.email,
        notes: action.payload.notes,
        activities: action.payload.activities
      }
    default:
      return state;
  }
}

export default userReducer;
